import React, { useState } from 'react';
import axios from 'axios';
import './AddInstitution.css';

const AddInstitution = () => {
  const [name, setName] = useState('');
  const [city, setCity] = useState('');
  const [courses, setCourses] = useState([]);
  const [ranking, setRanking] = useState('');
  const [image, setImage] = useState('');
  const [description, setDescription] = useState('');

  const courseOptions = [
    'Computer Science', 'Engineering', 'Business', 'Medicine', 'Arts', 'Law', 'Science', 'Mathematics'
  ];

  const handleCourseChange = (e) => {
    const selectedCourses = Array.from(e.target.selectedOptions, (option) => option.value);
    setCourses(selectedCourses);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newInstitution = {
      name,
      city,
      courses,
      ranking: parseFloat(ranking),
      image,
      description,
    };

    axios
      .post('http://localhost:5000/api/institutions/add', newInstitution)
      .then(() => {
        alert('Institution added!');
        setName('');
        setCity('');
        setCourses([]);
        setRanking('');
        setImage('');
        setDescription('');
      })
      .catch((err) => console.log(err));
  };

  return (
    <form className="add-institution-form" onSubmit={handleSubmit}>
      <label>Institution Name:</label>
      <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />

      <label>City:</label>
      <input type="text" value={city} onChange={(e) => setCity(e.target.value)} required />

      <label>Courses Offered:</label>
      <select multiple value={courses} onChange={handleCourseChange} required>
        {courseOptions.map((course) => (
          <option key={course} value={course}>{course}</option>
        ))}
      </select>

      <label>Ranking:</label>
      <input type="number" value={ranking} onChange={(e) => setRanking(e.target.value)} required />

      <label>Image URL:</label>
      <input type="text" value={image} onChange={(e) => setImage(e.target.value)} />

      <label>Description:</label>
      <textarea value={description} onChange={(e) => setDescription(e.target.value)} />

      <button type="submit">Add Institution</button>
    </form>
  );
};

export default AddInstitution;
