import React, { useState } from 'react';
import axios from 'axios';
import './AddInstitution.css';

const AddInstitution = () => {
  const [name, setName] = useState('');
  const [city, setCity] = useState('');
  const [course, setCourse] = useState('');
  const [ranking, setRanking] = useState('');
  const [image, setImage] = useState('');
  const [description, setDescription] = useState('');

  const courseOptions = [
    'Computer Science',
    'Information Technology',
    'AI & ML',
    'Data Science',
    'ECE',
    'EEE',
    'Civil',
    'Mech',
  ];

  const handleSubmit = (e) => {
    e.preventDefault();
    const newInstitution = {
      name,
      city,
      course,
      ranking: parseFloat(ranking),
      image,
      description,
    };

    axios
      .post('http://localhost:5000/api/institutions/add', newInstitution)
      .then(() => {
        alert('Institution added!');
        setName('');
        setCity('');
        setCourse('');
        setRanking('');
        setImage('');
        setDescription('');
      })
      .catch((err) => console.log(err));
  };

  return (
    <form className="add-institution-form" onSubmit={handleSubmit}>
      <label>Institution Name:</label>
      <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />

      <label>City:</label>
      <input type="text" value={city} onChange={(e) => setCity(e.target.value)} required />

      <label>Courses Offered:</label>
      <div className="radio-group">
        {courseOptions.map((courseOption, index) => (
          <label key={index} className="radio-label">
            <input
              type="radio"
              value={courseOption}
              checked={course === courseOption}
              onChange={(e) => setCourse(e.target.value)}
              required
            />
            {courseOption}
          </label>
        ))}
      </div>

      <label>Ranking:</label>
      <input type="number" value={ranking} onChange={(e) => setRanking(e.target.value)} required />

      <label>Institution Image URL:</label>
      <input type="text" value={image} onChange={(e) => setImage(e.target.value)} required />

      <label>Description:</label>
      <textarea value={description} onChange={(e) => setDescription(e.target.value)} />

      <button type="submit">Add Institution</button>
    </form>
  );
};

export default AddInstitution;
